import React, { useState } from 'react';
import './App.css';

const App = () => {
  const [quote, setQuote] = useState('');

  const fetchQuote = async () => {
    try {
      const response = await fetch('http://localhost:3001/random-quote');
      const data = await response.json();
      setQuote(data.quote);
    } catch (error) {
      console.error('Error fetching quote:', error);
      setQuote('Failed to load a quote. Please try again!');
    }
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(quote).then(() => {
      alert('Quote copied to clipboard!');
    }).catch((err) => {
      console.error('Failed to copy: ', err);
    });
  };

  return (
    <div className="App">
      <header>
        <h1>Rizz Skibidi Brain Rot Quote Generator</h1>
      </header>
      <main>
        <div className="quote-box">
          <p>{quote || 'Click the button to generate your first quote!'}</p>
        </div>
        <button onClick={fetchQuote}>Generate Quote</button>
        <button onClick={copyToClipboard} disabled={!quote}>Copy Quote</button>
      </main>
    </div>
  );
};

export default App;
